DROP SCHEMA IF EXISTS ringgold;
CREATE SCHEMA ringgold DEFAULT CHARACTER SET utf8 COLLATE utf8_bin;
USE ringgold;
CREATE TABLE alt_name (  Rec_ID bigint(20) NOT NULL auto_increment,  P_Code bigint(20) NOT NULL default '0',  Name varchar(250) NOT NULL default '',  Ext_Name varchar(250) default NULL,  City varchar(100) default NULL,  Ext_City varchar(100) default NULL,  Country varchar(10) NOT NULL default '',  Lang varchar(35) default NULL,  Notes varchar(60) default NULL,  Timestamp timestamp NOT NULL,  PRIMARY KEY (Rec_ID) ) Engine=MyISAM;
CREATE TABLE consortia (  Rec_ID bigint(20) NOT NULL auto_increment,  Consortia_ID bigint(30) NOT NULL default '0',  P_Code bigint(20) default NULL,  Name varchar(130) default NULL,  Timestamp timestamp NOT NULL,  PRIMARY KEY (Rec_ID) ) Engine=MyISAM;
CREATE TABLE deleted_id (  ID__No bigint(20) NOT NULL auto_increment,  old_id bigint(20) NOT NULL default '0',  new_id bigint(20) NOT NULL default '0',  details varchar(150) default NULL,  Timestamp timestamp NOT NULL,  PRIMARY KEY (ID__No) ) Engine=MyISAM;
CREATE TABLE identifiers ( Rec_ID bigint(20) NOT NULL auto_increment, P_Code bigint(20) NOT NULL default '0', Identifier varchar(30) NOT NULL default '', Value varchar(35) NOT NULL default '0', Timestamp timestamp NOT NULL, PRIMARY KEY (Rec_ID), KEY P_Code_3 (P_Code) ) Engine=MyISAM;
CREATE TABLE multi ( Rec_ID int(11) NOT NULL auto_increment, GP_Code bigint(20) NOT NULL default '0', P_Code bigint(20) NOT NULL default '0', Class varchar(20) NOT NULL default '', Number smallint(6) default NULL, Timestamp timestamp NOT NULL, PRIMARY KEY (Rec_ID) ) Engine=MyISAM;
CREATE TABLE notes  (  Rec_ID int(11) NOT NULL auto_increment,  P_Code bigint(20) NOT NULL default '0',  Notes varchar(250) NOT NULL default '',  Timestamp timestamp NOT NULL,  PRIMARY KEY (Rec_ID),  UNIQUE KEY P_Code (P_Code) ) Engine=MyISAM;
CREATE TABLE parents  (  Rec_ID bigint(20) NOT NULL auto_increment,  GP_Code bigint(20) default NULL default '0',  P_Code bigint(20) NOT NULL default '0',  Name varchar(250) NOT NULL default '',  Ext_Name varchar(250) default NULL,  City varchar(100) NOT NULL default '',  Ext_City varchar(100) default NULL,  Zip varchar(30) default NULL,  Country varchar(10) NOT NULL default '',  State varchar(25) default NULL,  Type varchar(35) NOT NULL default '',  Timestamp timestamp NOT NULL,  PRIMARY KEY (Rec_ID),  UNIQUE KEY P_Code (P_Code) ) Engine=MyISAM;
CREATE TABLE sizes  ( Rec_ID bigint(20) NOT NULL auto_increment, P_Code bigint(20) NOT NULL default '0', Kind varchar(30) NOT NULL default '', Value mediumint(9) NOT NULL default '0', Timestamp timestamp NOT NULL, PRIMARY KEY (Rec_ID), KEY P_Code_3 (P_Code) ) Engine=MyISAM;
CREATE TABLE tiers ( Rec_ID int(11) NOT NULL auto_increment, P_Code bigint(20) NOT NULL default '0', Tier_Type varchar(25) NOT NULL default '', Tier varchar(40) NOT NULL default '', Timestamp timestamp NOT NULL, PRIMARY KEY (Rec_ID), KEY P_Code_3 (P_Code) ) Engine=MyISAM;
CREATE TABLE types ( ID mediumint(9) NOT NULL auto_increment, Type varchar(50) default NULL, Def varchar(150) default NULL, Timestamp timestamp NOT NULL, PRIMARY KEY  (ID), UNIQUE KEY Demo (Type) ) Engine=MyISAM;
CREATE TABLE rg_tiers ( ID_No mediumint(9) NOT NULL auto_increment, RG_tier char(3) default NULL, RG_Tier_Description varchar(100) default NULL, Example text, Timestamp timestamp NOT NULL, PRIMARY KEY (ID_No) ) Engine=MyISAM;
CREATE TABLE url ( Rec_ID int(11) NOT NULL auto_increment, P_Code bigint(20) NOT NULL default '0', URL varchar(200) NOT NULL default '', alt_url tinyint(1) NOT NULL default '0', Timestamp timestamp NOT NULL, PRIMARY KEY (Rec_ID), KEY P_Code (P_Code) ) Engine=MyISAM;
